def dispPoint(p,cls):
    "Display a point and its associated class."
    print("{:8.5f} {:8.5f}  {:2d}".format(p[0],p[1],cls))

def displayData(s1,c1,s2,c2):
    "Display all of XN1 and XN2, interspersed."
    l1 = len(s1);  l2 = len(s2)
    l = max(l1,l2)
    for i in range(l):
        if i < l1:  dispPoint(s1[i],c1)
        if i < l2:  dispPoint(s2[i],c2)
